import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ThankYouPage.css';

const ThankYouPage = () => {
  const navigate = useNavigate();

  // Naviguer après quelques secondes si nécessaire, ou via un bouton
  setTimeout(() => {
    navigate('/login');
  }, 3000); // Rediriger après 3 secondes

  return (
    <div className="thankyou-container">
      <h1>Merci pour votre inscription !</h1>
      <p>Nous vous avons envoyé un e-mail de confirmation. Vous serez redirigé vers la page de connexion dans quelques secondes.</p>
    </div>
  );
}

export default ThankYouPage;
