import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './DonatePage.css';

const SignupPage = () => {
  const navigate = useNavigate(); // Hook pour la navigation

  // State pour le formulaire
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });

  // Fonction pour gérer les changements dans les champs du formulaire
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Fonction pour rediriger vers la page d'accueil
  const handleNavigateHome = () => {
    navigate('/'); // Redirection vers la page d'accueil
  };

  // Fonction pour rediriger vers la page de login
  const handleNavigateLogin = () => {
    navigate('/login'); // Redirection vers la page de login
  };

  // Fonction pour soumettre le formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Vérification des mots de passe
    if (formData.password !== formData.confirmPassword) {
      alert('Les mots de passe ne correspondent pas.');
      return;
    }

    // Envoi des données au backend via l'API
    const response = await fetch('http://localhost:5216/api/donors/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });

    if (response.ok) {
      alert('Inscription réussie');
      navigate('/login'); // Rediriger vers la page de connexion après inscription
    } else {
      alert('Erreur lors de l\'inscription');
    }
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">Logo</div>
        <div className="hhh">
          <ul>
            <li><a href="/">Accueil</a></li>
            <li><a href="/services">Services</a></li>
            <li><a href="/apropos">À propos</a></li>
            <li><a href="/contact">Contact</a></li>
            {/* Bouton pour revenir à l'accueil */}
            <button className="nav-button" onClick={handleNavigateHome}>Retour à l'accueil</button>
          </ul>
        </div>
      </nav>

      {/* Formulaire d'inscription */}
      <div className="form-container">
        <div className="form-box">
          <h2>Formulaire d'inscription</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-field">
              <label htmlFor="name">Nom complet</label>
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Votre nom"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-field">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Votre email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-field">
              <label htmlFor="phone">Numéro de téléphone</label>
              <input
                type="tel"
                id="phone"
                name="phone"
                placeholder="Votre numéro"
                value={formData.phone}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-field">
              <label htmlFor="password">Mot de passe</label>
              <input
                type="password"
                id="password"
                name="password"
                placeholder="Votre mot de passe"
                value={formData.password}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-field">
              <label htmlFor="confirmPassword">Confirmer le mot de passe</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                placeholder="Confirmer le mot de passe"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                required
              />
            </div>

            <button type="submit" className="cta-button">S'inscrire</button>
          </form>

          {/* Lien vers la page de login */}
          <div className="login-link">
            <p>Vous avez déjà un compte ? <button className="login-button" onClick={handleNavigateLogin}>Se connecter</button></p>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className='footer'>
        <div className="logo">Logo</div>
        {/* Liens du footer */}
        <div className='pa1'>
          <div className='lien-utils'>
            <p>Liens utiles</p>
            <ul>
              <li><a href="a propos">A propos</a></li>
              <li><a href="a propos">Contactez nous</a></li>
              <li><a href="a propos">Nos services</a></li>
              <li><a href="a propos">Blog</a></li>
              <li><a href="a propos">Evenements</a></li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignupPage;
